import React, { useEffect, useState } from 'react'

function ApplicatinonTabs() {
    const [prevValue, setPrevValue] = useState(0)



    const AppTabs = (e) => {
        const _figure = e.target.parentElement
        document.querySelectorAll(".application-tabs-items>div").forEach((item) => {
            item.children[0].style.border = `unset`;
            item.children[0].removeAttribute("class");
            item.children[0].removeAttribute("isComeFromLeftToRight");
        })
        _figure.style.border = ` 1px solid white `;
        _figure.classList.add("pointer-none")
        const figAttrNum = _figure.getAttribute("data-num");
        const figAttrInfo = _figure.getAttribute("data-info");
        const currentValue = figAttrNum;
        if (currentValue > prevValue) {
        } else {
            // document.querySelectorAll(".application-tabs-items>div").forEach((el) => {
            //     el.children[0].removeAttribute("isComeFromLeftToRight");
            // })
            _figure.setAttribute("isComeFromLeftToRight", "true")
        }
        setPrevValue(currentValue)
        // ************************************************************
        document.querySelectorAll(".application-tabs-slider>div").forEach((slider) => {
            const sliderAttrInfo = slider.getAttribute("data-info");
            slider.removeAttribute("class")
            if (_figure.getAttribute("isComeFromLeftToRight")) {
                if (sliderAttrInfo === figAttrInfo) {
                    slider.classList.add("left-to-right")
                }
            }
            else {
                slider.classList.remove("right-to-left")
                if (sliderAttrInfo === figAttrInfo) {
                    slider.classList.add("right-to-left")
                }
            }
        });
        // ******************************************************
    }
    // **************************************************************************************
    // useEffect(() => {
    //     document.querySelectorAll(".application-tabs-items>div").forEach((el) => {
    //         el.addEventListener("mouseenter", () => {
    //             el.removeAttribute("class")
    //             el.children[0].children[0].removeAttribute("class")
    //            const _src = el.children[3].src
    //             el.children[0].children[0].src = _src
    //             el.children[0].children[0].classList.add("changeItemspicFirst")
    //             el.classList.add("increase-item-pic-size")
    //         })
    //         // ***********
    //         el.addEventListener("mouseout" , ()=>{
    //             el.removeAttribute("class")
    //             el.children[0].children[0].removeAttribute("class")
    //             const _src = el.children[2].src
    //              el.children[0].children[0].src = _src;
    //              el.children[0].children[0].classList.add("changeItemspicSecond")
    //              el.classList.add("decrease-item-pic-size")
    //         })
    //     })
    // }, [])




    return (
        <section className='application-tabs-wrapper mt-10'>
            <div className='2xl:container 2xl:mx-auto'>

                <div className='application-tabs-items-wrapper flex justify-center'>
                    <div className='application-tabs-items w-[90%] flex flex-row-reverse border justify-evenly items-center'>

                        <div className='flex flex-col justify-center'>
                            <figure className='first-comein-item' data-num="1" data-info="my-irancell" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/06/MyIrancell-1.svg" alt="" /></figure>
                            <p className='hidden'>ایرانسل من</p>
                            <img className='hidden' src="https://irancell.ir/wp-content/uploads/2022/06/MyIrancell-1.svg" alt="" />
                            <img className='hidden' src="https://irancell.ir/wp-content/uploads/2023/01/My-Irancell-FA-1.svg" alt="" />
                        </div>

                        <div>
                            <figure data-num="2" data-info="lenz" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/03/Lenz.svg" alt="" /></figure>
                            <p className='hidden'>ایرانسل من</p>
                            <img className='hidden' src="https://irancell.ir/wp-content/uploads/2022/03/Lenz.svg" alt="" />
                            <img className='hidden' src="https://irancell.ir/wp-content/uploads/2022/03/Lenz-Active.svg" alt="" />
                        </div>

                        <div>
                            <figure data-num="3" data-info="dana-plus" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/03/DanaPlus.svg" alt="" /></figure>
                            <p className='hidden'>ایرانسل من</p>
                            <img className='hidden' src="https://irancell.ir/wp-content/uploads/2022/03/DanaPlus.svg" alt="" />
                            <img className='hidden' src="https://irancell.ir/wp-content/uploads/2022/03/DanaPlus-Active.svg" alt="" />
