import React, { useState } from 'react'
import Logo from "../../img/logo.png"


function NavRight() {
  const [menuHamburger , setMenuHamburger] = useState(false)

  const MenuHam = () =>{
    setMenuHamburger(!menuHamburger)
    console.log(menuHamburger);
    if(menuHamburger){
      document.querySelector(".new").style.height = "400px"
    }else{
      document.querySelector(".new").style.height = "unset"
    }
  }

  return (
    <div className='nav-right bg-yellow w-full lg:w-[80%] p-4 flex items-center'>
        <section className='flex flex-wrap w-full'>
            <div className='w-[60%] border flex'>
              <div className='w-[50%]'>
                <figure><img src={Logo} alt="" /></figure>
              </div>
              <div className='w-[50%] flex items-center'>
                <form className='w-full flex items-center form-menu relative'>
                <input className='w-full search' type="search" />
                <i className="bi bi-search"></i>
                </form>
              </div>
            </div>
            <div className='w-[40%] border flex items-center justify-end'>
              <i onClick={MenuHam} className={menuHamburger?(`bi bi-x-lg hamburger-menu`):(`bi  hamburger-menu `)}></i>
            </div>
        </section>
        <div className="new">
          righrgiore
        </div>
    </div>
  )
}

export default NavRight
