import React, { useEffect } from 'react';
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css/bundle";
import { EffectFlip, Pagination, Navigation } from "swiper";

function Shopping() {


    useEffect(() => {
        const _height = document.querySelector(".accordion-1 section").scrollHeight;
        document.querySelector(".accordion-1 section").style.height = `${_height}px`
        document.querySelector(".accordion-1").classList.add("active")
        document.querySelector(".accordion-1 i").style.transform = `rotateX(180deg)`
    }, [])

    document.querySelector(".accordion .swiper").addEventListener("click" , (e)=>{
            e.stopPropagation()
        })

    const accordionHandler = (e) => {
        e.stopPropagation()
        const current = e.currentTarget;
        const _height = current.querySelector("section").scrollHeight;

        current.classList.toggle("active")
        document.querySelectorAll(".accordion-items").forEach((para) => {
            if (para.getAttribute("data-id") !== current.getAttribute("data-id")) {
                para.classList.remove("active");
                para.querySelector("section").style.height = `0px`;
                para.querySelector("i").style.transform = `rotateX(0deg)`
            }
        })


        if (current.classList.contains("active")) {
            current.querySelector("section").style.height = `${_height}px`
            current.querySelector("i").style.transform = `rotateX(180deg)`
        } else {
            current.querySelector("section").style.height = `0px`;
            current.querySelector("i").style.transform = `rotateX(0deg)`
        }

    }






    return (
        <section className='shopping mt-9 px-2'>
            <div className='2xl:container 2xl:mx-auto'>

                <div className="flex flex-row-reverse justify-between">

                    <section>
                        <h1 className="fz-18">فروشگاه</h1>
                    </section>

                    <section>
                        <p className="self-yellow font-extrabold flex items-center"><i className="bi bi-chevron-left inline-flex mx-1"></i>برو به فروشگاه</p>
                    </section>

                </div>
                {/* ***********************start of accordion menu********************************************** */}

                <section className='accordion mt-7'>
                    <div className='accordion-items accordion-1' data-id="1" onClick={accordionHandler}>
                        <div className='shopping-details flex justify-between flex-row-reverse'>
                            <div className='flex flex-row-reverse items-center'>
                                <figure><img src="https://irancell.ir/wp-content/uploads/2022/03/internet-packages.svg" alt="" /></figure>
                                <p>بسته های اینترنت</p>
                            </div>

                            <div className='self-yellow flex items-center'><i className='bi bi-chevron-down inline-flex'></i></div>
                        </div>
                        <section>
                            <Swiper
                                effect={"flip"}
                                grabCursor={true}
                                pagination={true}
                                navigation={true}
                                modules={[EffectFlip, Pagination, Navigation]}
                                className="mySwiper"
                            >
                                <SwiperSlide>
                                    <img src="https://swiperjs.com/demos/images/nature-1.jpg" />
                                </SwiperSlide>
                                <SwiperSlide>
                                    <img src="https://swiperjs.com/demos/images/nature-2.jpg" />
                                </SwiperSlide>
                                <SwiperSlide>
                                    <img src="https://swiperjs.com/demos/images/nature-3.jpg" />
                                </SwiperSlide>
                                <SwiperSlide>
                                    <img src="https://swiperjs.com/demos/images/nature-4.jpg" />
                                </SwiperSlide>
                                <SwiperSlide>
                                    <img src="https://swiperjs.com/demos/images/nature-5.jpg" />
                                </SwiperSlide>
                                <SwiperSlide>
                                    <img src="https://swiperjs.com/demos/images/nature-6.jpg" />
                                </SwiperSlide>
                            </Swiper>
                        </section>
                    </div>

                    <div className='accordion-items my-5' data-id="2" onClick={accordionHandler}>
                        <div className='shopping-details flex justify-between flex-row-reverse'>
                            <div className='flex flex-row-reverse items-center'>
                                <figure><img src="https://irancell.ir/wp-content/uploads/2022/03/simcard.svg" alt="" /></figure>
                                <p>سیم کارت</p>
                            </div>

                            <div className='self-yellow flex items-center'><i className='bi bi-chevron-down inline-flex'></i></div>
                        </div>
                        <section>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Fugit vel quaerat, fugiat iste labore possimus dolorem laborum consequuntur quam blanditiis illo adipisci ex minima ipsam perferendis deleniti mollitia saepe aspernatur, voluptatem enim. Iusto provident cum sed. Sequi ducimus quae fuga eligendi cum sapiente omnis. Officia est maxime quo quibusdam expedita neque illo odio architecto iusto sed. Voluptatem ullam, et mollitia ipsam minima, quas nam repudiandae sunt, asperiores nulla porro iste!70</section>
                    </div>

                    <div className='accordion-items' data-id="3" onClick={accordionHandler}>
                        <div className='shopping-details flex justify-between flex-row-reverse'>
                            <div className='flex flex-row-reverse items-center'>
                                <figure><img src="https://irancell.ir/wp-content/uploads/2022/03/devices.svg" alt="" /></figure>
                                <p>دستگاه ها</p>
                            </div>

                            <div className='self-yellow flex items-center'><i className='bi bi-chevron-down inline-flex'></i></div>
                        </div>
                        <section>
                            Lorem ipsum dolor sit amet consectetur adipisicing elit. Ad necessitatibus dolor maiores animi ab. Atque voluptas asperiores saepe non fugit! Illo modi saepe explicabo magnam distinctio vero eius! Laudantium aut numquam adipisci, rerum ducimus, repellat natus, ab nemo architecto quaerat hic? Doloribus explicabo architecto minima est odit perferendis voluptatem doloremque molestiae, saepe dolor quas eaque deleniti, facere omnis corporis dolorem. Harum quos cupiditate provident rerum similique eveniet quibusdam nulla quisquam reprehenderit nobis commodi vel, totam aut quo, numquam a adipisci quia? Ad, eius magni ducimus sequi fugiat dolorum maiores hic doloremque alias officia velit tempora explicabo aspernatur quo ut amet!100
                        </section>
                    </div>

                </section>

            </div>
        </section>
    )
}

export default Shopping
