import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper';
import 'swiper/css/bundle';
import { useState } from 'react';

function SliderPage() {
    const [state, setState] = useState("")

    const HandleMouseEnter = () => {
        if (state !== "") {
            state.autoplay.stop()
        }
    }


    const HandleMouseLeave = () => {
        if (state !== "") {
            state.autoplay.start()
        }
    }


    const initSwiper = (swiper) => {
        setState(swiper)
    }
    return (
        <div className='w-full slider-container' onMouseEnter={HandleMouseEnter} onMouseLeave={HandleMouseLeave}>
            <Swiper onSwiper={initSwiper}
                slidesPerView={1}
                spaceBetween={0}
                autoplay={{
                    delay: 1500
                }}
                loop={true}
                pagination={{
                    clickable: true,
                }}
                navigation={true}
                modules={[Pagination, Navigation, Autoplay]}
                className="mySwiper"
            >
                <SwiperSlide>slider 1</SwiperSlide>
                <SwiperSlide>Slider 2</SwiperSlide>
                <SwiperSlide>Slider 3</SwiperSlide>
                <SwiperSlide>Slider 4</SwiperSlide>
                <SwiperSlide>Slider 5</SwiperSlide>
                <SwiperSlide>Slider 6</SwiperSlide>
                <SwiperSlide>Slider 7</SwiperSlide>
                <SwiperSlide>Slider 8</SwiperSlide>
                <SwiperSlide>Slider 2</SwiperSlide>
            </Swiper>
        </div>
    )
}

export default SliderPage
