import React, { useState } from 'react'

function ApplicatinonTabs() {
    const [prevValue , setPrevValue] = useState(0)



    const AppTabs = (e) => {
        const _figure =  e.target.parentElement
        document.querySelectorAll(".application-tabs-items>div").forEach((item)=>{
            item.children[0].style.border = `unset`
        })
        _figure.style.border = ` 1px solid white `;
        const figAttr = _figure.getAttribute("data-num");
        const currentValue = figAttr;
        if(currentValue>prevValue){
            console.log("greater");
        }else{
            console.log("less");
            document.querySelectorAll(".application-tabs-items>div").forEach((el)=>{
                el.children[0].removeAttribute("isComeFromLeftToRight")
              })
            _figure.setAttribute("isComeFromLeftToRight" , "true")
        }
        setPrevValue(currentValue)
    }



    return (
        <section className='application-tabs-wrapper mt-10'>
            <div className='2xl:container 2xl:mx-auto'>

                <div className='application-tabs-items-wrapper flex justify-center'>
                    <div className='application-tabs-items w-[90%] flex flex-row-reverse border justify-evenly items-center'>

                        <div><figure data-num="1" data-info="my-irancell" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/06/MyIrancell-1.svg" alt="" /></figure></div>
                        <div><figure data-num="2" data-info="lenz" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/03/Lenz.svg" alt="" /></figure></div>
                        <div><figure data-num="3" data-info="dana-plus" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/03/DanaPlus.svg" alt="" /></figure></div>
                        <div><figure data-num="4" data-info="pishvaz-music" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/03/Pishvaz.svg" alt="" /></figure></div>
                        <div><figure data-num="5" data-info="charkhooneh" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/03/Charkhoneh.svg" alt="" /></figure></div>
                        <div><figure data-num="6" data-info="kids-teenager" onClick={AppTabs}><img src="https://irancell.ir/wp-content/uploads/2022/11/Colorful-1-1.svg" alt="" /></figure></div>

                    </div>
                </div>

                <div className='application-tabs-slider'>
                    <div data-info="my-irancell">my irancell</div>
                    <div data-info="lenz">lenz</div>
                    <div data-info="dana-plus">dana plus</div>
                    <div data-info="pishvaz-music">pishvaz</div>
                    <div data-info="charkhooneh">charkhooneh</div>
                    <div data-info="kids-teenager">kids</div>
                </div>
            </div>
        </section>
    )
}

export default ApplicatinonTabs
